const buttonFeedback=document.querySelector(".button__feedback"),modal=document.querySelector(".modal"),buttonClose=document.querySelector(".modal__close");buttonFeedback.addEventListener("click",function(t){t.preventDefault(),modal.classList.add("modal--show")}),buttonClose.addEventListener("click",function(t){t.preventDefault(),modal.classList.remove("modal--show")});const tabs=document.querySelectorAll(".tabs__item"),tabsContent=document.querySelectorAll(".tabs__content");let tabName;function selectTabs(){tabs.forEach(t=>{t.classList.remove("tabs__item--active")}),this.classList.add("tabs__item--active"),selectTabsContent(tabName=this.getAttribute("data-tab"))}function selectTabsContent(t){tabsContent.forEach(e=>{e.getAttribute("data-tab")==t?e.classList.add("tabs__content--active"):e.classList.remove("tabs__content--active")})}tabs.forEach(t=>{t.addEventListener("click",selectTabs)});const questions=document.querySelectorAll(".faq__question");for(var i=0;i<questions.length;i++)questions[i].onclick=function(){this.nextElementSibling.classList.toggle("show"),this.classList.toggle("faq__question--active")};var swiper=new Swiper(".swiper-container",{navigation:{nextEl:".slider__toggle--prev",prevEl:".slider__toggle--next"}});